import React, { Component } from "react";
import { Tabs,Tab,Grid,Cell,Card,CardTitle, CardActions,Button,CardMenu, IconButton, CardText } from "react-mdl";


class Project extends Component{
    constructor(props) {
        super(props);
        this.state={activeTab:0}
    }
    toggleCategories() {
        if (this.state.activeTab === 0) {
            return (
                <Card shadow={5} style={{ minWidth: "450", margin: "auto" }}>
                    <CardTitle style={{ color: "#fff", height: "176px", background: "url(data:image/png;base64,)" }}>Project 1</CardTitle>
                    <CardText>
                        ffffdfhgdfgadsfgagasdfsdfasdfasdfasdfasdfasdfasdfasdfsadfsdfsdfsdfasdfasdfsdfsdfasdfasdfasdfasdfsadf/zz,
                        </CardText>
                        <CardActions border>
                            <Button colored>GitHub</Button>
                            <Button colored>Linkedin</Button>
                            <Button colored>Live Demo</Button>
                        </CardActions>
                                <CardMenu style={{ color: "#fff" }}>
                                    <IconButton name="share" />
                                </CardMenu>
                    
                </Card>
            )
        } else if (this.state.activeTab === 1) {
            return (
                <div><h1>This is Project 2</h1></div>
            )
            
        } else if (this.state.activeTab === 2) {
            return (
                <div><h1>Day Planner</h1></div>
            )
        } else if (this.state.activeTab === 3) {
            return (
                <div><h1>Random Generator</h1></div>
            )
        }
        
    }


    render() {
        return (
            <div className="category-tabs">

                <Tabs activeTab={this.state.activeTab} onChange={(tabId) => this.setState({ activeTab: tabId })} ripple>
                    <Tab>Project 1</Tab>
                    <Tab>Project 2</Tab>
                    <Tab>Day Planner</Tab>
                    <Tab>Random Generator</Tab>

                </Tabs>
                <section className="projects-grid">
                    <Grid className="projects-grid">
                        <Cell col={12}>
                            <div className="content">{this.toggleCategories()}></div>

                        </Cell>
                    </Grid>
                    
                </section>
            </div>
        )
    }

}
export default Project;
